mapper.update:
UPDATE account
account_name =#{accountName,jdbcType=VARCHAR}, account_password=#{accountPassword,jdbcType=VARCHAR}, account_phone =#{accountPhone,jdbcType=BIGINT}, account_email =#{accountEmail,jdbcType=VARCHAR}, account_status =#{accountStatus,jdbcType=BOOLEAN}, account_member_id =#{accountMember.memberId,jdbcType=BIGINT}, account_role_id =#{accountRoles.roleId,jdbcType=INTEGER},
WHERE a.account_id = #{accountId,jdbcType=BIGINT}

UPDATE account
SET account_password = #{newPassword}
WHERE account_id = #{accountId}

mapper.select:
SELECT

FROM account

    1=1

        and account_status =#{status}


LIMIT #{offset},#{limit}
